            string command = Console.ReadLine().ToLower().Trim();
            string[] commandTokens = command.Split(' ');

            if (commandTokens[0] == "encrypt" && commandTokens.Length == 3)
            {
                encrypt(commandTokens[1], commandTokens[2]);
            }
            else if (commandTokens[0] == "decrypt" && commandTokens.Length == 3)
            {
                decrypt(commandTokens[1], commandTokens[2]);
            }

            if (commandTokens[0] == "avragelön")
