        }
        return totalSalary / totalEmployees;
    }
    static void encrypt(string key, string input)
    {
        Words w = new Words();
        char[] word = input.ToCharArray();
        for (int i = 0; i < word.Length; i++)
        {
            word[i] = (char)(word[i] + 1);
        }
        input = new string(word);
        w.input = input;
        w.key = key;
        words.Add(w);
        System.Console.WriteLine($"word was added with the key {key}");
    }
    static string nonSafeEncrypt(string input)
    {
        char[] word = input.ToCharArray();
        for (int i = 0; i < word.Length; i++)
        {
            word[i] = (char)(word[i] + 1);
        }
        input = new string(word);
        return input;
    }
    static void decrypt(string key, string input)
    {
        string userword = nonSafeEncrypt(input);
        bool wordWasFound = false;
        bool keyWasRight = false;
        for (int i = 0; i < words.Count; i++)
        {
            if (words[i].input == userword)
            {
                wordWasFound = true;
            }
            if (words[i].key == key)
            {
                keyWasRight = true;
            }
            if (wordWasFound && keyWasRight)
            {
                Words w = new Words();
                char[] word = userword.ToCharArray();
                for (int j = 0; j < word.Length; j++)
                {
                    word[j] = (char)(word[j] - 1);
                }
                input = new string(word);
                Console.WriteLine($"word was resolved as: {input}");
                return;
            }
        }
        if (!wordWasFound)
        {
            Console.WriteLine("word not found");
        }
        if (!keyWasRight)
        {
            Console.WriteLine("wrong key");
        }
    }
